create table RESULT (id integer identity primary key, symbol varchar(100) not null, execution_date timestamp not null, quote_date timestamp not null,change double not null, price_change double not null, window_size integer not null, quote_count integer not null)
create table ACTION (id integer identity primary key, result_id integer references RESULT(id) on delete cascade, quote_date timestamp not null, side varchar(20) not null)